/**
 * Get directory to globally installed npm packages
 * e.g. `/Users/name/.config/yarn/global/node_modules`
 */
export const getDirectory: () => string

/**
 * Check if a path is a child path of yarn global node_modules directory (returned by `getDirectory()`)
 */
export const inDirectory: (path: string) => boolean

/**
 * Packages installed by `yarn global add`
 */
export const getDependencies: () => string[]

/**
 * Check if a package is installed by `yarn global add`
 */
export const hasDependency: (name: string) => boolean

/**
 * Check if a package is inside yarn global node_modules directory (returned by `getDirectory()`)
 */
export const hasPackage: (name: string) => boolean
